import React, {useEffect, useState} from 'react';
import './NewMultiItemCarousel.css'

import {Link} from "react-router-dom";

import Carousel from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';

import pic1 from '../images/multiCarousel/c1.jpg'
import pic2 from '../images/multiCarousel/c2.jpg'
import pic3 from '../images/multiCarousel/c3.jpg'
import pic4 from '../images/multiCarousel/c4.jpg'
import pic5 from '../images/multiCarousel/c5.jpg'
import pic6 from '../images/multiCarousel/c6.jpg'
import loggedReducer from "../../reducers/isSigned";


const NewMultiItemCarousel = () => {

    const [data, setData] = useState([])
    // const [loading, setLoading] = useState(true)


    //'http://localhost:5000/api/songs'
    useEffect(() => {

        fetch('http://localhost:5000/api/showSongs')
            .then(res => res.json())
            .then(data => (setData(data.reverse())))
            .catch(err => console.log(err))

    }, [])

    var vh;
    var imageHeight = window.innerHeight;
    // vh = imageHeight / 4 - (imageHeight / 100) + 'px';
    vh = imageHeight / 4 + 'px';


    return (
        <Carousel
            // className="pt-4"
            arrows
            slidesPerPage={6}
            slidesPerScroll={1}
            infinite
            keepDirectionWhenDragging
            breakpoints={{
                // smaller than
                1200: {
                    slidesPerPage: 5,
                    // arrows: false,
                },
                1024: {
                    slidesPerPage: 4,
                    // arrows: false,
                },
                770: {
                    slidesPerPage: 3,
                    arrows: false,
                    infinite: false
                },
                500: {
                    slidesPerPage: 2,
                    arrows: false,
                    // centered: true
                    infinite: false
                }

            }}
        >

            {data.map((item, i) =>
                <div key={i}>
                    <Link
                        className="song-links"
                        to={{
                            pathname: `/EditSong`,
                            state: {
                                songId: item._id
                            }
                        }}
                    >
                        <img src={"http://localhost:5000/" + item.imageURL} alt={item.songName}
                             style={{height: vh, width: vh}} className="songImage"/>
                    </Link>
                    <div className=" text-center d-block">
                        <Link
                            className="song-links"
                            to={{
                                pathname: `/EditSong`,
                                state: {
                                    songId: item._id
                                }
                            }}
                        >
                            <h5>{item.songName}</h5>
                        </Link>
                        <p>{item.singer}</p>
                    </div>
                </div>
            )}

        </Carousel>
    );
}

export default NewMultiItemCarousel;
