import React, {useState, useEffect} from 'react';
import {useLocation} from 'react-router';
import {Link} from "react-router-dom";

import ReactPlayer from 'react-player'
import '../DisplaySongs.css'


const DisplaySongs = () => {
    const [data, setData] = useState([])
    // const [loading, setLoading] = useState(true)

    let location = useLocation();
    // console.log(location.state.songId);
    const songId = location.state.songId.toString();

    useEffect(() => {
        fetch(`http://localhost:5000/api/showSong/${songId}`)
            .then(res => res.json())
            .then(res => setData(res))
            .catch(err => console.log(err))

    }, [])

    const DeleteSong = () => {
        return fetch(`http://localhost:5000/api/showSong/${songId}`, {method: 'DELETE'})
            .then(res => res.json())
            .then(res => {
                console.log('Deleted:', res.message)
                return res
            })
            .catch(err => console.error(err))
    }

    return (
        <div className="container row">
            <div className="bg-light mb-5 col-sm-9">

                <p className="text-center pt-2"> {data.songName} آهنگ جدید </p>
                <p className="text-center"> {data.singer} از</p>

                <button onClick={DeleteSong} className="btn btn-danger m-1"><Link to='/admin'>Delete</Link></button>

                <img width="100%" src={"http://localhost:5000/" + data.imageURL} alt=""/>
                <a target="_blank" href={"http://localhost:5000/" + data.songURL}><p
                    className="text-center">دانلود</p></a>
                <ReactPlayer
                    url={"http://localhost:5000/" + data.songURL}
                    playing={false}
                    controls={true}
                    light={false}
                    height={40}
                    width={'100%'}
                />
                <p>{data.views} views</p>
                <p>category: {data.category}</p>
            </div>
            <div className="col-sm-3 pt-5">
                <form encType="multipart/form-data" onSubmit={handleSubmit}>
                    {/*<label htmlFor="name">Name: </label>*/}
                    <div className="form-group">
                        <input
                            className="btn-block text-center"
                            placeholder=" اسم آهنگ"
                            type="text"
                            name="song"
                            onChange={e => setSongName(e.target.value)}
                            value={songName}
                        />
                    </div>

                    <div className="form-group">
                        <input
                            className="btn-block text-center"
                            placeholder=" اسم خواننده"
                            type="text"
                            name="singer"
                            onChange={e => setSinger(e.target.value)}
                            value={singer}
                        />
                    </div>

                    <div className="form-group">
                        <input
                            className="btn-block text-center"
                            placeholder="دسته بندی"
                            type="text"
                            name="category"
                            onChange={e => setCategory(e.target.value)}
                            value={category}
                        />
                    </div>

                    {/*<label htmlFor="file">File: </label>*/}
                    <div className="custom-file was-validated mb-3">
                        <label className="custom-file-label text-center" htmlFor="validatedCustomFile">فایل موزیک</label>
                        <input
                            placeholder="Music file"
                            type="file"
                            name="song"
                            onChange={handleSelectedSongFile}
                            className="custom-file-input"
                            id="validatedCustomFile"
                            required
                        />
                        <div className="invalid-feedback">Pick an audio or mp3 file no bigger than 15mb</div>
                    </div>

                    <div className="custom-file was-validated mb-3">
                        <label className="custom-file-label text-center" htmlFor="validatedCustomFile">عکس آهنگ</label>
                        <input
                            placeholder="Cover Photo"
                            type="file"
                            name="photo"
                            onChange={handleSelectedPhoto}
                            className="custom-file-input"
                            id="validatedCustomFile"
                            required

                        />
                        <div className="invalid-feedback">Pick an image which is jpeg or png and not bigger than 5mb</div>
                    </div>


                    <button
                        className="btn btn-primary btn-block"
                        type="submit"
                    >

                        Submit


                    </button>

                </form>
            </div>
        </div>
    );
}

export default DisplaySongs;
