import React, { useState, useEffect } from 'react';

import ReactPlayer from 'react-player'
import './DisplaySongs.css'
import {Link} from "react-router-dom";


const DisplaySongs = () => {
    const [data, setData] = useState([])
    // const [loading, setLoading] = useState(true)

    //'http://localhost:5000/api/songs'
    useEffect(() => {

        fetch('http://localhost:5000/api/showSongs')
            .then(res => res.json())
            .then(data => (setData( data.reverse()), console.log(data)))
            .catch(err => console.log(err))

    }, [])


    return (
        <div>

            {

                data.map((item, i) =>
                    <div key={i} className="bg-light mb-5 ">

                        <Link to={"http://localhost:5000/" + data._id }>دامه مطلب</Link>
                        <p className="text-center pt-2"> {item.songName} آهنگ جدید </p>
                        <p className="text-center"> {item.singer} از</p>
                        <img width="100%" src={"http://localhost:5000/"+item.imageURL} alt=""/>
                        <a target="_blank" href={"http://localhost:5000/"+item.songURL}><p className="text-center">Download</p></a>
                        <ReactPlayer
                            url={"http://localhost:5000/"+item.songURL}
                            playing={false}
                            controls={true}
                            light={false}
                            height={40}
                            width={'100%'}
                        />

                    </div>
                )
            }

        </div>
    );
}

export default DisplaySongs;
