import React, {useState, useEffect, useCallback} from 'react';

const OtherSongsFromSinger = (props) => {
    const [data2, setData2] = useState([])

    var singerName = props.data.singer

    const getOtherSong = useCallback(() => {

        //fetch(`http://localhost:5000/api/showSongsBySinger/singer?singer=${singerName}`)
        fetch(`http://localhost:5000/api/showSongsBySinger/singer?singer=محی`)
            .then(res => res.json())
            .then(data2 => (setData2(data2.reverse())))
            .catch(err => console.log(err))

    }, [props.data])

    return (
        <div className="row">

            {console.log(typeof singerName)}
            {console.log(data2)}
            {data2.map((item, i) =>
                <div key={i} className="col-12">
                    <img src={"http://localhost:5000/"+item.imageURL} alt={item.songName}  className="songImage" style={{ width: '300px', height: '300px'}}/>
                    <div className=" text-center d-block">
                        <h5>{item.songName}</h5>
                        <p>{item.singer}</p>
                        <p>{item.views} بازدید: </p>
                    </div>
                </div>
            )}


        </div>
    );
};

export default OtherSongsFromSinger;
