import React, {useState, useEffect} from 'react';
import {Link} from 'react-router-dom';

import ReactPlayer from 'react-player'
import './DisplaySongs.css'


const DisplaySongs = () => {
    const [data, setData] = useState([])
    // const [loading, setLoading] = useState(true)

    //'http://localhost:5000/api/songs'
    useEffect(() => {

        fetch('http://localhost:5000/api/showSong/:id')
            .then(res => res.json())
            .then(data => (setData(data.reverse()), console.log(data)))
            .catch(err => console.log(err))

    }, [])


    return (
        <div>


            <div className="bg-light mb-5 offset-1">
                <Link to={"http://localhost:5000/" + data.id}>دامه مطلب</Link>
                <p className="text-center pt-2"> {data.songName} آهنگ جدید </p>
                <p className="text-center"> {data.singer} از</p>
                <img width="100%" src={"http://localhost:5000/" + data.imageURL} alt=""/>
                <a target="_blank" href={"http://localhost:5000/" + data.songURL}><p
                    className="text-center">Download</p></a>
                <ReactPlayer
                    url={"http://localhost:5000/" + data.songURL}
                    playing={false}
                    controls={true}
                    light={false}
                    height={40}
                    width={'100%'}
                />

            </div>
            )


        </div>
    );
}

export default DisplaySongs;
