import React from 'react';

import pic1 from '../images/img (31).jpg'
import pic2 from '../images/img (33).jpg'
import pic3 from '../images/img (35).jpg'
import ReactPlayer from "react-player";

const data = [ {image:pic1, songName:"Im fly", singer:"Ali"}, {image:pic2, songName:"Im cold", singer:"Moniii"}, {image:pic3, songName:"Im sad", singer:"Mohi"}];

const  indicator = (i) =>{
    if(i===0){
        return "active"
    }else {
        return ""
    }
};

const  imageItem = (i) =>{
    if(i===0){
        return "carousel-item active"
    }else {
        return "carousel-item"
    }
};

const CarouselTop = () => {

    return (
        <div>
            <div id="carouselCaptions" className="carousel slide" data-ride="carousel">
                <ol className="carousel-indicators d-xs-none">
                    {data.map((item, i) =>
                        <li key={i} data-target="#carouselCaptions" data-slide-to={i} className={indicator(i)}></li>
                    )}
                </ol>
                <div className="carousel-inner">
                    {data.map((item, i) =>
                        <div key={i} className={imageItem(i)}  >
                            <img src={item.image} className="d-block w-100" alt="..."/>
                            <div className="carousel-caption d-none d-md-block">
                                <h5>{item.songName}</h5>
                                <p>{item.singer}</p>
                            </div>
                            <div className="carousel-caption-small text-center d-block d-md-none" >
                                <h5>{item.songName}</h5>
                                <p>{item.singer}</p>
                            </div>
                        </div>
                    )}


                </div>
                <a className="carousel-control-prev" href="#carouselCaptions" role="button" data-slide="prev">
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="sr-only">Previous</span>
                </a>
                <a className="carousel-control-next" href="#carouselCaptions" role="button" data-slide="next">
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="sr-only">Next</span>
                </a>
            </div>
        </div>
    );
};

export default CarouselTop;
