import React, {useEffect, useState} from 'react';
import './NewMultiItemCarousel.css'

import {Link} from "react-router-dom";

import Carousel from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';

import pic1 from '../images/multiCarousel/c1.jpg'
import pic2 from '../images/multiCarousel/c2.jpg'
import pic3 from '../images/multiCarousel/c3.jpg'
import pic4 from '../images/multiCarousel/c4.jpg'
import pic5 from '../images/multiCarousel/c5.jpg'
import pic6 from '../images/multiCarousel/c6.jpg'
import loggedReducer from "../../reducers/isSigned";


const NewMultiItemCarousel = () => {

    const [data, setData] = useState([])
    // const [loading, setLoading] = useState(true)


    //'http://localhost:5000/api/songs'
    useEffect(() => {

        fetch('http://localhost:5000/api/showSongs')
            .then(res => res.json())
            .then(data => (setData(data.reverse())))
            .catch(err => console.log(err))

    }, [])

    var vh;
    var imageHeight = window.innerHeight;
    // vh = imageHeight / 4 - (imageHeight / 100) + 'px';
    vh = imageHeight / 4  + 'px';
    let vw = '30vw';
    const viewWidth2 = () => {
        if (window.innerWidth < 464) {
            vw = '50vw'

        } else if (window.innerWidth < 700) {
            vw = '33vw'

        }else if (window.innerWidth < 1024 ) {
            vw = '25vw'


        }
        else if (window.innerWidth >= 1024) {
            vw = '16vw'

        }

        console.log(vw)
    };


    const viewWidth = () => {

        var imageHeight = window.innerHeight;
        vh = imageHeight / 4 + 'px';

        console.log(imageHeight)
        console.log(vh)
    };

    return (
        <Carousel
            // className="pt-4"
            arrows
            slidesPerPage={6}
            slidesPerScroll={1}
            infinite
            keepDirectionWhenDragging
            breakpoints={{
                // smaller than
                1200: {
                    slidesPerPage: 5,
                    // arrows: false,
                },
                1024: {
                    slidesPerPage: 4,
                    // arrows: false,
                },
                770: {
                    slidesPerPage: 3,
                    arrows: false,
                    infinite: false
                },
                500: {
                    slidesPerPage: 2,
                    arrows: false,
                    // centered: true
                    infinite: false
                }

            }}
        >
            {viewWidth2()}
            {data.map((item, i) =>
                <div key={i} >
                    <img src={"http://localhost:5000/"+item.imageURL} alt={item.songName} style={{height: vw, width: vw}} className="songImage"/>
                    <div className=" text-center d-block">
                        <h5>{item.songName}</h5>
                        <p>{item.singer}</p>
                    </div>
                </div>
            )}


            {/*<div>*/}
            {/*    <img src={pic1} style={{ height: vh}}/>*/}
            {/*    <div className=" text-center d-block">*/}
            {/*        <h5>TODAY'S</h5>*/}
            {/*        <p>TOP HITS</p>*/}
            {/*    </div>*/}
            {/*</div>*/}
