            {/*    <div className=" text-center d-block">*/}
            {/*        <h5>TODAY'S</h5>*/}
            {/*        <p>TOP HITS</p>*/}
            {/*    </div>*/}
            {/*</div>*/}
            {/*<div>*/}
            {/*    <img src={pic3} style={{ height: vh}}/>*/}
            {/*    <div className=" text-center d-block">*/}
            {/*        <h5>TODAY'S</h5>*/}
            {/*        <p>TOP HITS</p>*/}
            {/*    </div>*/}
            {/*</div>*/}
            {/*<div>*/}
            {/*    <img src={pic4} style={{ height: vh}}/>*/}
            {/*    <div className=" text-center d-block">*/}
            {/*        <h5>TODAY'S</h5>*/}
            {/*        <p>TOP HITS</p>*/}
            {/*    </div>*/}
            {/*</div>*/}
            {/*<div>*/}
            {/*    <img src={pic5} style={{ height: vh}}/>*/}
            {/*    <div className=" text-center d-block">*/}
            {/*        <h5>TODAY'S</h5>*/}
            {/*        <p>TOP HITS</p>*/}
            {/*    </div>*/}
            {/*</div>*/}
            {/*<div>*/}
            {/*    <img src={pic6} style={{ height: vh}}/>*/}
            {/*    <div className=" text-center d-block">*/}
            {/*        <h5>TODAY'S</h5>*/}
            {/*        <p>TOP HITS</p>*/}
            {/*    </div>*/}
            {/*</div>*/}


            {/*<img src={pic2} style={{ height: vh}} />*/}
            {/*<img src={pic3} style={{ height: vh}} />*/}
            {/*<img src={pic4} style={{ height: vh}} />*/}
            {/*<img src={pic5} style={{ height: vh}} />*/}
            {/*<img src={pic6} style={{ height: vh}} />*/}
        </Carousel>
    );
}

export default NewMultiItemCarousel;
